commits.content:
* {
    box-sizing: border-box;
}
body {
    background-image: url(loan.jpg);
    background-size: cover;
    background-repeat: round;
}
.logo {
    font-size: 20px;
    color: white;
    position: absolute;
}
nav ul {
    margin: auto;
    text-align: center;
}
nav li {
    display: inline-block;
}
nav a {
    text-decoration: none;
    padding: 1rem 1.5rem;
    color: white;
    font-family: Verdana, Geneva, Tahoma, sans-serif;
}
h3 {
    margin-top: 40px;
    color: whitesmoke;
    font-size: 22px;
    font-weight: lighter;
    margin-left: 100px;
}
.container {
    display: flex;
}

.request-box {
    height: 290px;
    width: 330px;
    background: #ccca;
    margin:auto;
    border-radius: 10px;
    margin-bottom: 20px;
}
form {
    width: 100%;
    margin-left: 20px;

}
form input{
    display: flex;
    margin-top: 10px;
    border-radius: 8px;
    border: none;
    width: 80%;
    padding: 8px;
}
input[type="button"]{
    width: 100px;
    width: 80%;
    justify-content: center;
    font-size: 15px;
    color: white;
    background: lightseagreen;
}
h5 {
    text-align: center;
    font-size: 20px;
    font-weight: lighter;
}
div P {
    text-align: center;
    font-size: 15px;
}
form p {
    color: blue;
    text-align: left;
}
section {
    height:230px;
    background-color: darkgrey;
    text-align: center;
    margin-bottom: 100px;
}
section h6 {
    color: blue;
    font-size: 20px;
    font-weight: lighter;
    text-align: center;
}
section p {
    text-align: center;
    font-size: 10px;
}
section div {
    color: white;
    display: inline-block;
    margin: 10px;
    padding: 20px;
    background: blue;
}
span button {
    color: white;
    background: none;
    margin-left: 100px;
}
